import React, { Component } from 'react';
import { connect } from "react-redux";
import IntlMessages from "../../../components/utility/intlMessages";

// Style
import { Content, Center, GuestInfo } from './GuestPortal.style';

class GuestDetails extends Component{
  render() {
    fetch('https://glacial-fortress-60211.herokuapp.com/https://bv-online-assessment.herokuapp.com/api/bookings')
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.log('Authorization failed : ' + error.message));

    let responseData;

    return (
      <Content>
        <Center>
          <h1><IntlMessages id = {'custApp.yourBookingCode'}></IntlMessages></h1>
        </Center>
        <Center>
          <h3 id="fail-validation-message"></h3>
        </Center>

        <Center>
          <input id="cust-app-input"></input>
          <button onClick={async () => {
            let inputString = document.getElementById("cust-app-input").value;
            let failValidation = false;
            for (let i = 0, len = inputString.length; i < len; i++) {
              let code = inputString.charCodeAt(i);
              if (!(code > 47 && code < 58) && // numeric (0-9)
                  !(code > 64 && code < 91) && // upper alpha (A-Z)
                  !(code > 96 && code < 123)) { // lower alpha (a-z)
                document.getElementById("fail-validation-message").style.opacity = 1;
                document.getElementById("fail-validation-message").innerHTML = "Should be combination of numbers & alphabets";
                failValidation = true;
                break;
              }
            }

            if (!failValidation){
              let responseValid = true;
              await fetch('https://glacial-fortress-60211.herokuapp.com/https://bv-online-assessment.herokuapp.com/api/bookings/' + inputString)
              .then(response => response.json())
              .then(data => responseData = data)
              .catch(error => {
                console.log('Authorization failed : ' + error.message);
                document.getElementById("fail-validation-message").style.opacity = 1;
                document.getElementById("fail-validation-message").innerHTML = "Booking code not identified";
                responseValid = false;
              }
              );

              if (responseValid){
                document.getElementById("fail-validation-message").style.opacity = 0;
                document.getElementById("guest-details-wrapper").style.opacity = 1;
                console.log(responseData);
              }
            }
            
          }}>Enter</button>
        </Center>

        <br/><br/><br/>

        <GuestInfo id="guest-details-wrapper">
          <div>Hi, <b>{responseData.guest_name}</b></div>
          <div>Thank you for booking with Bukit Vista. Here are the details of your current booking: </div>
          <div>Property name: </div>
          <div>Check in date: </div> <div>Check out date:</div>
          <div>Arrival timeL</div>
        </GuestInfo>

      </Content>
    )
  }
}

export default connect(state => ({
  ...state.App,
  locale: state.LanguageSwitcher.language.locale,
  height: state.App.height
}))(GuestDetails);
