import React, { Component } from 'react';
import { connect } from "react-redux";
import IntlMessages from "../../../components/utility/intlMessages";

// Style
import { Content, Center } from './GuestPortal.style';

const validateInput = () => {
  let inputString = document.getElementById("custAppInput");
  console.log(inputString);
  // for (i = 0, len = inputString.length; i < len; i++) {
  //   code = inputString.charCodeAt(i);
  //   if (!(code > 47 && code < 58) && // numeric (0-9)
  //       !(code > 64 && code < 91) && // upper alpha (A-Z)
  //       !(code > 96 && code < 123)) { // lower alpha (a-z)
  //     return false;
  //   }
  // }
  // return true;
}

class GuestDetails extends Component{
  render() {
    fetch('https://glacial-fortress-60211.herokuapp.com/https://bv-online-assessment.herokuapp.com/api/bookings')
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.log('Authorization failed : ' + error.message));

    return (
      <Content>
        <Center>
          <h1><IntlMessages id = {'custApp.yourBookingCode'}></IntlMessages></h1>
        </Center>
        <Center>
          <input id="custAppInput"></input>
          <button onClick="validateInput()">Enter</button>
        </Center>
      </Content>
    )
  }
}

export default connect(state => ({
  ...state.App,
  locale: state.LanguageSwitcher.language.locale,
  height: state.App.height
}))(GuestDetails);
